using namespace std; 

string reverseString(string s) 
{ 
    int n = s.length(); 

    // Swap character starting from two 
    // corners 
    for (int i = 0; i < n / 2; i++) 
        swap(s[i], s[n - i - 1]); 

    return s; 
} 

// Driver program 
int main() 
{ 
    string s = "hello"; 
    s = reverseString(s); 
    cout << "Reversed string is : "
         << s; 
    return 0; 
}

