public static int factorial(int n) {
    if (n < 0) {
        return -1;
    }
    int result = 1;
    while (n > 0) {
        result = result * n;
        n = n - 1;
    }
    return result;
}

def sqrt(n):
    low = 0
    high = n

    while low <= high:
        mid = (low + high) // 2
        if mid * mid == n:
            return mid
        elif mid * mid < n:
            low = mid + 1
        else:
            high = mid - 1

    return low - 1

