// Function to reverse any sequence starting with pointer 
// begin and ending with pointer end 
void Reverse(char* begin, char* end) 
{ 
    char temp; 
    while (begin < end) { 
        temp = *begin; 
        *begin++ = *end; 
        *end-- = temp; 
    } 
} 

// Function to reverse words*/ 
void ReverseWords(char* s) 
{ 
    char* word_begin = s; 

    // Word boundary 
    char* temp = s; 

    // Revert string 
    Reverse(s, temp); 

    // Revert each word 
    while (*temp) { 
        temp++; 
        if (*temp == '\0') { 
            Reverse(word_begin, temp - 1); 
        } else if (*temp == ' ') { 
            Reverse(word_begin, temp - 1); 
            word_begin = temp + 1; 
        } 
    } 
} 

// Driver code 
