function encrypt(message,key) { 
	let encryptedMessage = ""; 
	for (let i = 0; i < message.length; i++) { 
		let charCode = message.charCodeAt(i); 
		if ((charCode > 64 && charCode < 91) || (charCode > 96 && charCode < 123)) { 
