function listFirstTenPrimes() {
  let primeNumbers = [];
  let index = 2;
  while (primeNumbers.length < 10) {
    let isPrime = true;
    for (let i = 2; i <= Math.sqrt(index); i++) {
      if (index % i === 0) {
        isPrime = false;
        break;
      }
    }
    if (isPrime) {
      primeNumbers.push(index);
    }

    index++;
  }

  return primeNumber;
}

